    y = np.arange(len(saliency_sums))
    ax_curve.fill_betweenx(y, 0, saliency_sums, color='#a9cce3', alpha=0.8, linewidth=3)

    plt.tight_layout()
    plt.savefig(output_path, bbox_inches='tight')
