def collect_enrichment_with_ratios_per_gene(cancer_type, tag, source, top_n=20, gene_label=None):
    """
    Collect enrichment terms and compute gene ratios.

    Args:
        cancer_type (str)
        tag (str)
        source (str): e.g. "REAC"
        top_n (int)
        gene_label (str): If given, load file for a specific gene label.
    """
    path = f"results/gene_prediction/enrichment/{cancer_type}_{tag}_{gene_label}_{source}_enrichment.csv"
    if not os.path.exists(path):
        return []

    df = pd.read_csv(path)
    df = df.sort_values(by='p_value', ascending=True).head(top_n)

    terms = []
    for _, row in df.iterrows():
        term_name = row['name']
        genes = row['intersection'].split(',')
        ratio = len(genes) / int(row['term_size'])
        terms.append((term_name, genes, ratio, row['p_value']))
    
    return terms

def draw_dot_plot_with_ratio_per_gene(terms, cancer_type, source, top_n=20, label=None):
    """
    Draw a dot plot of enrichment terms with gene ratio.

    Args:
        terms: List of (term_name, genes, ratio, p_value)
        cancer_type: e.g. "PAAD"
        source: e.g. "REAC"
        top_n: Max number of terms
        label: Optional label (e.g., gene name like "PEX5")
    """
    import matplotlib.pyplot as plt
    import seaborn as sns

    if not terms:
        return

    df = pd.DataFrame(terms[:top_n], columns=["Term", "Genes", "Ratio", "PValue"])
    df = df.sort_values("Ratio", ascending=True)

    plt.figure(figsize=(6, 0.5 * len(df)))
    sns.scatterplot(
        data=df,
        x="Ratio",
        y="Term",
        size="Ratio",
        hue="PValue",
        palette="viridis",
        sizes=(50, 300),
        legend=False
    )
    plt.xlabel("Gene Ratio")
    plt.ylabel("Enriched Term")
    plt.title(f"{source} enrichment: {label} ({cancer_type})")
    plt.tight_layout()

    #fname = f"results/gene_prediction/enrichment/plot_{cancer_type}_{label}_{source}.pdf"
    fname = f"results/gene_prediction/enrichment/plot_{cancer_type}_{label}_{source}.png"
    plt.savefig(fname)
    plt.close()

