def collect_enrichment_with_ratios_per_gene_(cancer_type, tag, source, top_n=20, gene_label=None):
    """
    Collect enrichment terms and compute gene ratios.

    Args:
        cancer_type (str)
        tag (str)
        source (str): e.g. "REAC"
        top_n (int)
        gene_label (str): If given, load file for a specific gene label.

    Returns:
        List of tuples: (term_name, genes, ratio, p_value)
    """
    path = f"results/gene_prediction/enrichment/{cancer_type}_{tag}_{gene_label}_{source}_enrichment.csv"
    if not os.path.exists(path):
        return []

    df = pd.read_csv(path)
    df = df.sort_values(by='p_value', ascending=True).head(top_n)

    terms = []
    for _, row in df.iterrows():
        term_name = row['name']
        genes = row['intersection_size'].split(',')
        ratio = len(genes) / int(row['term_size'])
        terms.append((term_name, genes, ratio, row['p_value']))
    
    return terms

def draw_dot_plot_with_ratio_per_gene_(terms, cancer_type, source, top_n=20, label=None):
    """
    Draw a dot plot of enrichment terms with gene ratio.

    Args:
        terms: List of (term_name, genes, ratio, p_value)
        cancer_type: e.g. "PAAD"
        source: e.g. "REAC"
        top_n: Max number of terms
        label: Optional label (e.g., gene name like "PEX5")
    """
    if not terms:
        return

    df = pd.DataFrame(terms[:top_n], columns=["Term", "Genes", "Ratio", "PValue"])
    df = df.sort_values("Ratio", ascending=True)

    plt.figure(figsize=(6, 0.5 * len(df)))
    sns.scatterplot(
        data=df,
        x="Ratio",
        y="Term",
        size="Ratio",
        hue="PValue",
        palette="viridis",
        sizes=(50, 300),
        legend=False
    )
    plt.xlabel("Gene Ratio")
    plt.ylabel("Enriched Term")
    plt.title(f"{source} enrichment: {label} ({cancer_type})")
    plt.tight_layout()

    fname = f"results/gene_prediction/enrichment/plot_{cancer_type}_{label}_{source}.png"
    plt.savefig(fname)
    plt.close()

def collect_enrichment_with_ratios_per_gene(cancer_type, tag, source, top_n=20, gene_label=None):
    path = f"results/gene_prediction/enrichment/{cancer_type}_{tag}_{gene_label}_{source}_enrichment.csv"
    if not os.path.exists(path):
        return []

    df = pd.read_csv(path)
    df = df.sort_values(by='p_value', ascending=True).head(top_n)

    terms = []
    for _, row in df.iterrows():
        term_name = row['name']
        intersection_size = int(row['intersection_size'])
        term_size = int(row['term_size'])
        
        # Since we don't have actual gene names, we use a dummy list of the same gene repeated
        genes = [gene_label] * intersection_size

        ratio = intersection_size / term_size if term_size else 0
        terms.append((term_name, genes, ratio, row['p_value']))

    return terms

def draw_dot_plot_with_ratio_per_gene(terms, cancer_type, source, top_n=20, label=None):
    """
    Draw a dot + stacked bar plot of enrichment terms for a single gene.
    
    Args:
        terms: List of (term_name, genes, ratio, p_value)
        cancer_type: e.g., "BRCA"
        source: e.g., "GO:BP"
        top_n: Top N terms to plot
        label: Gene label, e.g. "PEX5"
    """
    if not terms:
        print(f"‚ö†Ô∏è No enrichment terms for {label} ({cancer_type}, {source})")
        return

    # Convert to DataFrame
    df = pd.DataFrame(terms, columns=["Term", "Genes", "GeneRatio", "PValue"])
    df["LogP"] = -np.log10(df["PValue"].clip(lower=1e-10))
    df["Cluster"] = 0  # single gene gets single cluster
    df["Color"] = df["Cluster"].map(CLUSTER_COLORS)

    # Select top terms by LogP
    top_terms = df.sort_values("LogP", ascending=False)["Term"].unique()[:top_n]
    df = df[df["Term"].isin(top_terms)]

    term_order = list(reversed(top_terms))  # bottom-to-top order
    df["Term"] = pd.Categorical(df["Term"], categories=term_order, ordered=True)

    # === Setup Figure ===
    fig = plt.figure(figsize=(18, 0.6 * len(term_order)))
    gs = fig.add_gridspec(1, 2, width_ratios=[1.0, 1], wspace=0.05)

    # === Dot Plot ===
    ax1 = fig.add_subplot(gs[0])
    sns.scatterplot(
        data=df,
        x="GeneRatio", y="Term",
        size="LogP", hue="Cluster",
        palette=CLUSTER_COLORS,
        sizes=(50, 300),
        edgecolor="black", linewidth=0.5,
        ax=ax1,
        legend=False
    )
    ax1.set_xscale("log")
    ax1.set_xlabel("\nGene Ratio", fontsize=52)
    ax1.set_ylabel("", fontsize=44)
    ax1.set_title(f"{source} ‚Äî {label}\n", fontsize=56)
    ax1.tick_params(axis='both', labelsize=44)
    ax1.grid(axis="x", linestyle="--", alpha=0.5)
    ax1.set_yticklabels(ax1.get_yticklabels(), fontsize=52)

    # === Stacked Bar Plot ===
    ax2 = fig.add_subplot(gs[1], sharey=ax1)
    LABEL_PAD = 0.8
    MIN_BAR_FOR_INSIDE_LABEL = 1.2

    for term in term_order:
        row = df[df["Term"] == term].iloc[0]
        y = term
        logp = row["LogP"]
        cluster = row["Cluster"]
        color = CLUSTER_COLORS[cluster]

        ax2.barh(
            y=y,
            width=logp,
            left=0,
            height=0.6,
            color=color,
            edgecolor="black"
        )

        text_color = "black" if logp >= MIN_BAR_FOR_INSIDE_LABEL else "white"
        ax2.text(
            x=logp / 2 if logp >= MIN_BAR_FOR_INSIDE_LABEL else -LABEL_PAD,
            y=y,
            s=f"{logp:.1f}",
            va="center",
            ha="center" if logp >= MIN_BAR_FOR_INSIDE_LABEL else "right",
            fontsize=36,
            color=text_color,
            fontweight="bold"
        )

    # Set limits and labels
    xmin = min(0, df["LogP"].min() - 1)
    xmax = df["LogP"].max() + 1
    ax2.set_xlim(xmin, xmax)
    ax2.set_xlabel("\n-log10(p-value)", fontsize=52)
    ax2.tick_params(axis='x', labelsize=40)
    ax2.tick_params(axis='y', labelleft=False)
    ax2.grid(axis='x', linestyle='--', alpha=0.5)
    ax2.set_xticklabels(ax2.get_xticklabels(), fontsize=44)

    # === Final Layout ===
    plt.subplots_adjust(left=0.3, wspace=0.05, top=0.98, bottom=0.02)
    ax1.set_ylim(-0.5, len(term_order) - 0.5)
    ax2.set_ylim(-0.5, len(term_order) - 0.5)

    out_path = Path(f"results/gene_prediction/enrichment/plot_{cancer_type}_{label}_{source}.png")
    out_path.parent.mkdir(parents=True, exist_ok=True)
    plt.savefig(out_path, dpi=300, bbox_inches="tight")
    plt.close()
    print(f"‚úÖ Saved dot+bar plot for {label}: {out_path}")

def plot_enrichment_for_genes(
    gene_list_dict,
    cancer_type,
    tag,
    sources=None,
    top_n=20
):
    """
    Run enrichment for specific genes and plot dot plots (REAC, KEGG, etc.).

    Args:
        gene_list_dict (dict): Dictionary {label: [genes]}.
        cancer_type (str): Cancer label (e.g., "PAAD").
        tag (str): Tag for output naming (e.g., "novel").
        sources (list): Enrichment sources. Defaults to ['REAC', 'KEGG', 'HP', 'GO:BP'].
        top_n (int): Number of top terms to plot.

    Returns:
        final_gene_to_pathways: Dictionary {gene_label: set of enriched terms}
    """
    if sources is None:
        sources = ['REAC', 'KEGG', 'HP', 'GO:BP']

    # Step 1: Run enrichment
    _ = run_enrichment_for_genes(gene_list_dict, cancer_type, tag)

    # Step 2: Plot
    final_gene_to_pathways = defaultdict(set)

    for label in gene_list_dict:
        for source in sources:
            print(f"üìä Plotting {source} enrichment for {label} in {cancer_type}...")

            terms = collect_enrichment_with_ratios_per_gene(
                cancer_type=cancer_type,
                tag=tag,
                source=source,
                top_n=top_n,
                gene_label=label
            )

            if terms:
                draw_dot_plot_with_ratio_per_gene(
                    terms=terms,
                    cancer_type=cancer_type,
                    source=source,
                    top_n=top_n,
                    label=label
                )

                for term in terms:
                    term_name = term[0]
                    final_gene_to_pathways[label].add(term_name)

    return final_gene_to_pathways


def run_enrichment_for_genes(gene_list_dict, cancer_type, tag):
    """
    Run functional enrichment for each gene list in the dictionary and save results.

    Args:
        gene_list_dict (dict): Dictionary {label: list of gene symbols}.
        cancer_type (str): Cancer label (e.g. "PAAD").
        tag (str): Tag for output file naming (e.g. "novel").

    Returns:
        output_files (list): List of saved CSV file paths.
    """
    gp = GProfiler(return_dataframe=True)
    output_files = []

    for label, gene_list in gene_list_dict.items():
        try:
            result = gp.profile(
                organism="hsapiens",
                query=gene_list,
                sources=["REAC", "KEGG", "GO:BP", "HP"],
                user_threshold=0.05,
                significance_threshold_method="fdr"
            )
            if not result.empty:
                for source in ["REAC", "KEGG", "GO:BP", "HP"]:
                    filtered = result[result["source"] == source]
                    path = f"results/gene_prediction/enrichment/{cancer_type}_{tag}_{label}_{source}_enrichment.csv"
                    os.makedirs(os.path.dirname(path), exist_ok=True)
                    filtered.to_csv(path, index=False)
                    output_files.append(path)
        except Exception as e:
            print(f"‚ùå Enrichment failed for {label}: {e}")

    return output_files

def run_survival_analysis(
    survival_path,
    expr_path,
    node_names_topk,
    row_labels,
    output_dir,
    cluster_threshold="median",
    clusters_to_plot=[0, 1, 2, 3]
):


    os.makedirs(output_dir, exist_ok=True)

    survival_df = pd.read_csv(survival_path, sep="\t")
    survival_df['_PATIENT'] = survival_df['_PATIENT'].str.upper()

    expr_df = pd.read_csv(expr_path, sep="\t", index_col=0)
    expr_df.index = expr_df.index.str.upper()
    expr_df.columns = expr_df.columns.str.upper().str[:12]

    cluster_to_genes = defaultdict(list)
    for gene, label in zip(node_names_topk, row_labels):
        cluster_to_genes[label].append(gene.upper())

    patient_cluster_scores = pd.DataFrame(index=expr_df.columns)
    for cl, genes in cluster_to_genes.items():
        valid_genes = list(set(genes) & set(expr_df.index))
        if not valid_genes:
            print(f"‚ö†Ô∏è No valid genes found for cluster {cl}. Skipping.")
            continue
        patient_cluster_scores[f'cluster_{cl}'] = expr_df.loc[valid_genes].mean(axis=0)

    merged = survival_df.merge(patient_cluster_scores, left_on="_PATIENT", right_index=True)
    if merged.empty:
        print("‚ùå No matching patient IDs between expression and survival data.")
        return

    for cl in clusters_to_plot:
        col = f"cluster_{cl}"
        if col not in merged.columns:
            print(f"‚ö†Ô∏è Cluster column {col} not found. Skipping.")
            continue

        T = merged["OS.time"]
        E = merged["OS"]
        threshold = merged[col].median() if cluster_threshold == "median" else merged[col].mean()
        group_col = f"group_{cl}"
        merged[group_col] = (merged[col] >= threshold).astype(int)

        kmf = KaplanMeierFitter()
        fig, ax = plt.subplots(figsize=(8, 6))

        for group in [0, 1]:
            label = f"{col} {'Low' if group == 0 else 'High'}"
            kmf.fit(T[merged[group_col] == group], E[merged[group_col] == group], label=label)
            kmf.plot_survival_function(ci_show=False, ax=ax, linewidth=2)

        try:
            result = logrank_test(
                T[merged[group_col] == 1],
                T[merged[group_col] == 0],
                event_observed_A=E[merged[group_col] == 1],
                event_observed_B=E[merged[group_col] == 0]
            )
            pval = result.p_value
        except Exception as e:
            print(f"Log-rank test failed: {e}")
            pval = float('nan')

        ci_df = kmf.confidence_interval_
        ci_width = (ci_df.iloc[:, 1] - ci_df.iloc[:, 0]).mean()

        ax.set_title(f"Survival by {col} Expression", fontsize=18)
        ax.set_xlabel("Time (Days)", fontsize=16)
        ax.set_ylabel("Survival probability", fontsize=16)
        ax.tick_params(axis='both', labelsize=12)

        legend = ax.get_legend()
        if legend:
            legend.remove()


        # Fit Cox model for this cluster only
        cox_df = merged[[ "OS.time", "OS", col ]].copy()
        cox_df.columns = ["time", "event", "cluster_score"]

        cph_single = CoxPHFitter()
        cph_single.fit(cox_df, duration_col="time", event_col="event")
        hr = cph_single.hazard_ratios_["cluster_score"]

        # Mean CI already computed above: ci_width

        # ‚úÖ Annotation block: CI ‚Üí HR ‚Üí p-value
        text_x = 0.03
        text_y_start = 0.12
        text_y_step = 0.045

        # CI on top
        ax.text(
            text_x, text_y_start,
            f"CI = {ci_width:.3f}",
            transform=ax.transAxes,
            fontsize=16,
            verticalalignment='center'
        )

        # HR next
        ax.text(
            text_x, text_y_start - text_y_step,
            f"HR = {hr:.3f}",
            transform=ax.transAxes,
            fontsize=16,
            verticalalignment='center'
        )

        # p-value at bottom
        ax.text(
            text_x, text_y_start - 2 * text_y_step,
            f"p = {pval:.4g}",
            transform=ax.transAxes,
            fontsize=16,
            verticalalignment='center'
        )



        plt.tight_layout()
        plot_path = os.path.join(output_dir, f"km_cluster_{cl}.png")
        plt.savefig(plot_path, dpi=300)
        plt.close()

    cox_cols = [c for c in patient_cluster_scores.columns if c in merged.columns]
    cox_df = merged[["OS.time", "OS"] + cox_cols].copy()
    cox_df.columns = ["time", "event"] + cox_cols

    cph = CoxPHFitter()
    cph.fit(cox_df, duration_col="time", event_col="event")
    cph.summary.to_csv(os.path.join(output_dir, "cox_summary.csv"))
    print(f"üìÑ Saved Cox summary: {os.path.join(output_dir, 'cox_summary.csv')}")

    return cph.summary, merged, patient_cluster_scores

# _high_low
def perform_survival_analysis_per_cluster(
    survival_path: str,
    expression_matrix_path: str,
    cluster_labels: np.ndarray,
    top_gene_names: list,
    output_dir: str,
    min_samples_per_group=5,
    cluster_colors=None  # Optional: dict { 'high': color, 'low': color }
):
    print("üîç Starting survival analysis ‚Äî High vs Low, separate plots...")

    os.makedirs(output_dir, exist_ok=True)

    survival_df = pd.read_csv(survival_path, sep='\t')
    survival_df['sample'] = survival_df['sample'].str.strip().str[:15]
    survival_df = survival_df.dropna(subset=['OS', 'OS.time'])
    survival_df = survival_df.drop_duplicates(subset='sample')

    expr_df = pd.read_csv(expression_matrix_path, sep='\t', index_col=0)
    expr_df.index = expr_df.index.str.upper()
    expr_df.columns = [col.strip().upper()[:15] for col in expr_df.columns]

    top_gene_names = [g.upper() for g in top_gene_names]
    expr_df = expr_df.loc[expr_df.index.intersection(top_gene_names)]

    if expr_df.empty:
        print("‚ùå No top-k genes found in expression matrix.")
        return

    gene_to_cluster = {gene.upper(): cluster_labels[i] for i, gene in enumerate(top_gene_names)}
    expr_df['cluster'] = expr_df.index.map(gene_to_cluster)

    cluster_expr_df = pd.DataFrame(index=expr_df.columns)
    for cluster_id in sorted(np.unique(cluster_labels)):
        genes = expr_df[expr_df['cluster'] == cluster_id].drop(columns='cluster').index
        if len(genes) == 0:
            continue
        cluster_expr_df[f'cluster_{cluster_id}'] = expr_df.loc[genes].mean(axis=0)

    merged_df = survival_df.merge(cluster_expr_df, left_on='sample', right_index=True, how='inner')
    if merged_df.empty:
        print("‚ùå No overlapping patients between survival and expression data.")
        return

    for cluster_id in sorted(np.unique(cluster_labels)):
        col = f'cluster_{cluster_id}'
        if col not in merged_df.columns:
            print(f"‚ö†Ô∏è Cluster column {col} not found. Skipping.")
            continue

        cluster_expr = merged_df[col]
        threshold = cluster_expr.median()
        high_mask = cluster_expr >= threshold
        low_mask = cluster_expr < threshold

        if high_mask.sum() < min_samples_per_group or low_mask.sum() < min_samples_per_group:
            print(f"‚ö†Ô∏è Cluster {cluster_id} skipped due to insufficient samples.")
            continue

        T_high, E_high = merged_df.loc[high_mask, 'OS.time'], merged_df.loc[high_mask, 'OS']
        T_low, E_low = merged_df.loc[low_mask, 'OS.time'], merged_df.loc[low_mask, 'OS']

        fig, ax = plt.subplots(figsize=(8, 6))
        kmf = KaplanMeierFitter()

        # Plot HIGH group
        kmf.fit(T_high, E_high, label="High Expression")
        kmf.plot(ax=ax, ci_show=False,
                 color=cluster_colors.get('high', '#1f77b4') if cluster_colors else None,
                 linewidth=2)

        # Plot LOW group
        kmf.fit(T_low, E_low, label="Low Expression")
        kmf.plot(ax=ax, ci_show=False,
                 color=cluster_colors.get('low', '#ff7f0e') if cluster_colors else None,
                 linewidth=2)

        # Average CI width
        kmf.fit(T_high, E_high)
        ci_df_high = kmf.confidence_interval_
        ci_width_high = (ci_df_high.iloc[:, 1] - ci_df_high.iloc[:, 0]).mean()

        kmf.fit(T_low, E_low)
        ci_df_low = kmf.confidence_interval_
        ci_width_low = (ci_df_low.iloc[:, 1] - ci_df_low.iloc[:, 0]).mean()

        try:
            result = logrank_test(T_high, T_low, event_observed_A=E_high, event_observed_B=E_low)
            pval = result.p_value
        except Exception as e:
            print(f"‚ö†Ô∏è Log-rank test failed for cluster {cluster_id}: {e}")
            pval = np.nan

        # === ANNOTATION ===
        # Vertical stack: p-value on top, CI below it, same x, same left align
        text_x = 0.03
        text_y_start = 0.12
        text_y_step = 0.045

        ax.text(
            text_x, text_y_p,
            f"p = {pval:.4g}",
            transform=ax.transAxes, fontsize=16,
            verticalalignment='center'
        )
        ax.text(
            text_x, text_y_ci,
            f"CI High = {ci_width_high:.3f}\nCI Low = {ci_width_low:.3f}",
            transform=ax.transAxes, fontsize=16,
            verticalalignment='top'
        )

        if ax.get_legend():
            ax.get_legend().set_title(f"Cluster {cluster_id}")

        ax.set_xlabel("Time (Days)", fontsize=16)
        ax.set_ylabel("Survival probability", fontsize=16)
        ax.set_title(f"Cluster {cluster_id}: High vs Low Expression", fontsize=16)
        ax.tick_params(axis='both', which='major', labelsize=12)

        plt.tight_layout()

        save_path = os.path.join(output_dir, f"km_cluster_{cluster_id}_high_low.png")
        plt.savefig(save_path, dpi=300)
        plt.close()

        print(f"‚úÖ Saved KM plot for cluster {cluster_id} to {save_path}")

    print("‚úÖ‚úÖ Done: separate plots for all clusters.")

