    # Default color if no labels provided
    if row_labels_bio is None:
        row_labels_bio = np.zeros(relevance_matrix_bio.shape[0], dtype=int)

    unique_clusters = np.unique(row_labels_bio)
    color_map = [CLUSTER_COLORS.get(int(c % len(CLUSTER_COLORS)), '#808080') for c in unique_clusters]

    for i, (omics, (start, end)) in enumerate(omics_splits.items()):
        ax = fig.add_subplot(2, 2, i + 1, projection='3d')

        # Slice omics subspace
        data = relevance_matrix_bio[:, start:end]
        data_3d = PCA(n_components=3).fit_transform(data)

        # Light gray background for each pane
        ax.set_facecolor('#f0f0f0')
        for axis in (ax.xaxis, ax.yaxis, ax.zaxis):
            axis.pane.set_facecolor((0.95, 0.95, 0.95, 1.0))
            axis.pane.set_edgecolor('black')

        # Plot per cluster
        for c_idx, cluster in enumerate(unique_clusters):
            cluster_mask = row_labels_bio == cluster
            ax.scatter(
                data_3d[cluster_mask, 0],
                data_3d[cluster_mask, 1],
                data_3d[cluster_mask, 2],
                s=10,
                alpha=0.8,
                color=color_map[c_idx],
                label=f"Cluster {cluster}"
            )

        # Grid and style
        ax.grid(True, color='gray', linestyle='--', linewidth=0.8)
        ax.set_title(f"{omics.upper()} Subspace", fontsize=12, fontweight='bold')
        ax.xaxis.set_major_locator(plt.MaxNLocator(5))
        ax.yaxis.set_major_locator(plt.MaxNLocator(5))
        ax.zaxis.set_major_locator(plt.MaxNLocator(5))
        ax.tick_params(colors='black', labelsize=8)
        ax.set_box_aspect([1, 1, 1])

    # Common title
    fig.suptitle("Cluster Distribution Across Omics-Specific Subspaces", fontsize=14, fontweight='bold')

    # Add legend outside the subplots
    handles, labels = ax.get_legend_handles_labels()
    fig.legend(handles, labels, loc='upper center', ncol=len(unique_clusters)//2, fontsize=9, frameon=False)

    plt.tight_layout(rect=[0, 0, 1, 0.92])

    if save_path:
        plt.savefig(save_path, dpi=300, bbox_inches='tight')
    plt.show()


