def compute_subspace_similarity(matrix_a, matrix_b, n_components=3, save_csv_path=None):
    """
    Quantify alignment between two PCA subspaces using principal angles and save results to CSV.

    Parameters
    ----------
    matrix_a : np.ndarray
        First embedding matrix (e.g., biological relevance features).
    matrix_b : np.ndarray
        Second embedding matrix (e.g., topological relevance features).
    n_components : int, optional (default=3)
        Number of principal components to use for 3D PCA comparison.
    save_csv_path : str, optional
        Path to save CSV file containing principal angles and cosine similarities.

    Returns
    -------
    dict
        Dictionary with principal angles (degrees), mean angle, and mean cosine similarity.
    """
    # === PCA projection ===
    pca_a = PCA(n_components=n_components).fit(matrix_a)
    pca_b = PCA(n_components=n_components).fit(matrix_b)

    # === Compute subspace angles and cosine similarities ===
    angles_rad = subspace_angles(pca_a.components_.T, pca_b.components_.T)
    angles_deg = np.degrees(angles_rad)
    cos_similarities = np.cos(angles_rad)

    # === Aggregate results ===
    results = {
        "principal_angles_deg": angles_deg,
        "mean_angle_deg": angles_deg.mean(),
        "mean_cosine_similarity": cos_similarities.mean()
    }

    # === Optional CSV export ===
    if save_csv_path is not None:
        df = pd.DataFrame({
            "Principal_Component": [f"PC{i+1}" for i in range(len(angles_deg))],
            "Angle_deg": angles_deg,
            "Cosine_Similarity": cos_similarities
        })
        df.loc[len(df)] = ["Mean", results["mean_angle_deg"], results["mean_cosine_similarity"]]
        
        os.makedirs(os.path.dirname(save_csv_path), exist_ok=True)
        df.to_csv(save_csv_path, index=False)
        print(f"üìÅ Subspace comparison results saved to: {save_csv_path}")

    return results

